import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const User = (props) => (
  <tr>
    <td>{props.user.fullname}</td>
    <td>{props.user.sex}</td>
    <td>{props.user.phone}</td>
    <td>{props.user.email}</td>
    <td>
      <Link to={"/edituser/" + props.user._id}>edit</Link>
    </td>
  </tr>
);

export default class profile extends Component {
  constructor(props) {
    super(props);

    this.deleteUser = this.deleteUser.bind(this);

    this.state = {
      cards: [],
      search: "",
      cetagory: "",
      name: "",
      place: "",
      organizer: "",
      date: "",
      fullname: "",
      sex: "",
      born: "",
      phone: "",
      email: "",
      check: false,
    };
    this.setSearch = this.setSearch.bind(this);
    this.confirm = this.confirm.bind(this);
    this.apply = this.apply.bind(this);
  }

  apply(props) {
    this.setState({ check: true });
    console.log(props);
    this.setState({ cetagory: props.cards.cetagory });
    this.setState({ name: props.card.name });
    this.setState({ place: props.cards.place });
    this.setState({ organizer: cards.exercise.organizer });
    this.setState({ date: props.cards.date });
  }
  onSubmit(e) {
    e.preventDefault();

    const cards = {
      cetagory: this.cards.cetagory,
      name: this.cards.name,
      place: this.cards.place,
      organizer: this.cards.organizer,
      date: this.cards.date,

      fullname: e.target.fullname,
      sex: e.target.sex,
      born: e.target.born,
      phone: e.target.phone,
      email: e.target.email,
    };
    console.log(cards);

    axios
      .post("http://localhost:5000/cards/search", cards)
      .then((res) => console.log(res.data));

    window.location = "/search";
    this.setState({ check: false });
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/users/")
      .then((response) => {
        this.setState({ users: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteUser(id) {
    axios.delete("http://localhost:5000/users/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      users: this.state.users.filter((el) => el._id !== id),
    });
  }

  userList() {
    return this.state.users.map((currentuser) => {
      return (
        <User
          user={currentuser}
          deleteUser={this.deleteUser}
          key={currentuser._id}
        />
      );
    });
  }
  setSearch(e) {
    this.setState({ search: e.target.value });
    console.log(this.state.search);
  }
  confirm() {
    const { search } = this.state;
    console.log(search);
    axios
      .get("http://localhost:5000/cards/search/" + search)
      .then((response) => {
        this.setState({ cards: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
    console.log(search);
  }

  render() {
    return (
      <>
        <div class="container">
          <h1>Profile Page</h1>
          <br />
          <div className="row">
            <div className="col-10">
              <input
                class="form-control mr-sm-2"
                type="text"
                placeholder="Search exercise..."
                name=""
                onChange={this.setSearch}
              />{" "}
            </div>
            <div className="col-2">
              <button
                className="btn btn-primary"
                onClick={() => this.confirm()}
              >
                <span>search</span>
              </button>
            </div>
          </div>
          <br />
          <div class="form-group">
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>cetagory</th>
                  <th>รายการการแข่งขัน</th>
                  <th>สถานที่</th>
                  <th>ผู้จัด</th>
                  <th>dateที่จัดงาน</th>
                  <th>USERNAME</th>
                  <th>BD</th>
                  <th>SEX</th>
                  <th>PHONE</th>
                  <th>EMAIL</th>
                </tr>
              </thead>
              <tbody>{this.userList()}</tbody>
            </table>
          </div>
        </div>
      </>
    );
  }
}
