import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const Exercise = (props) => (
  <div class="row align-items-center no-gutters mb-4 mb-lg-5">
    <div class="col-xl-7 col-lg-7">
      <img class="img-fluid mb-7 mb-lg-0" src={props.exercise.image} alt="" />
    </div>
    <div class="col-xl-5 col-lg-5">
      <div class="featured-text text-center text-lg-left">
        <h3 class="text-black">{props.exercise.cetagory}</h3>
        <h4 class="text-black">{props.exercise.name}</h4>
        <h6 class="mb-0 text-black-60">The place: {props.exercise.place}</h6>
        <h6 class="mb-0 text-black-60">
          Organizer: {props.exercise.organizer}
        </h6>
        <h6 class="mb-0 text-black-60">
          Date: {props.exercise.date.substring(0, 10)}
        </h6>
        {/* <p class="mb-0 text-black-60">Detail: {props.exercise.detail}</p> */}
        <br />
        <Link
          class="btn btn-primary js-scroll-trigger"
          to={"/showcompetition/" + props.exercise._id}
        >
          ShowDetail
        </Link>{" "}
        |{" "}
          <button class="btn btn-primary js-scroll-trigger" onClick={props.apply(props)}>Apply</button>
      </div>
    </div>
  </div>
);

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.deleteExercise = this.deleteExercise.bind(this);

    this.state = {
      exercises: [],
      search: "",
      check: false,
    };
    this.setSearch = this.setSearch.bind(this);
    this.confirm = this.confirm.bind(this);
    this.apply = this.apply.bind(this);

  }
  apply(props){
    this.setState({check:true});
    console.log()
  
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/exercises/")
      .then((response) => {
        this.setState({ exercises: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteExercise(id) {
    axios.delete("http://localhost:5000/exercises/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      exercises: this.state.exercises.filter((el) => el._id !== id),
    });
  }

  exerciseList() {
    return this.state.exercises.map((currentexercise) => {
      return (
        <Exercise
          exercise={currentexercise}
          deleteExercise={this.deleteExercise}
          apply={this.apply}
          key={currentexercise._id}
        />
      );
    });
  }

  setSearch(e) {
    this.setState({ search: e.target.value });
    console.log(this.state.search);
  }
  confirm() {
    const { search } = this.state;
    console.log(search);
    axios
      .get("http://localhost:5000/exercises/search/" + search)
      .then((response) => {
        this.setState({ exercises: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
    console.log(search);
  }

  render() {
    return (
      <>
        <div className="container">
          <h1>Home</h1>
          <br />
          <div className="row">
            <div className="col-10">
              <input
                class="form-control mr-sm-2"
                type="text"
                placeholder="Search exercise..."
                name=""
                onChange={this.setSearch}
              />{" "}
            </div>
            <div className="col-2">
              <button
                className="btn btn-primary"
                onClick={() => this.confirm()}
              >
                <span>search</span>
              </button>
            </div>
          </div>
        </div>

        {this.state.check==true&&<div class="form-group">
        <form onSubmit={this.onSubmit}>
              <div class="row">
                <div class="col-xl-6 col-lg-6">
                  <div className="form-group">
                    <label>Fullname : </label>
                    <input
                      type="text"
                      required
                      className="form-control"
                      // value={this.state.username}
                      // onChange={this.onChangeUsername}
                      placeholder="กรุณากรอกชื่อ-นามสกุล"
                    />
                  </div>
                </div>
                <div class="col-xl-3 col-lg-3">
                  <div className="form-group">
                    <label>BD: </label>
                    <div>
                      <DatePicker
                      // selected={this.state.date}
                      // onChange={this.onChangeDate}
                      />
                    </div>
                  </div>
                </div>
                <div class="col-xl-3 col-lg-3">
                  <div className="form-group">
                    <label>Sex: </label>
                    <input
                      type="text"
                      required
                      className="form-control"
                      placeholder="กรุณากรอกเพศ"
                    />
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-xl-6 col-lg-6">
                  <div className="form-group">
                    <label>Email: </label>
                    <input
                      type="text"
                      required
                      className="form-control"
                      // value={this.state.password}
                      // onChange={this.onChangePassword}
                      placeholder="กรุณากรอกอีเมล"
                    />
                  </div>
                </div>

                <div class="col-xl-6 col-lg-6">
                  <div className="form-group">
                    <label>Phone Number: </label>
                    <input
                      type="text"
                      required
                      className="form-control"
                      // value={this.state.phone}
                      // onChange={this.onChangePhone}
                      placeholder="กรุณากรอกเบอร์โทรศัพท์"
                    />
                  </div>
                </div>
              </div>

              <div className="form-group">
                <input
                  type="submit"
                  value="submit"
                  className="btn btn-primary"
                />
              </div>
            </form>
            </div>}

        <section className="projects-section bg-light" id="projects">
        {this.state.check==false&&<div className="container">{this.exerciseList()}</div>}
        </section>
      </>
    );
  }
}
