import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Exercise = (props) => (
  <div class="col-md-4 text-center animate-box">
    <div class="product">
      <img class="img-fluid mb-4 " src={props.exercise.image} alt="" />
      <div class="inner">
        <p>
          <a href="single.html" class="icon">
            <i class="icon-shopping-cart"></i>
          </a>
          <a href="single.html" class="icon">
            <i class="icon-eye"></i>
          </a>
        </p>
      </div>
    </div>
    <div class="desc">
      <h4 class="text-black">{props.exercise.name}</h4>
      <p class="mb-0 text-black-60">The place: {props.exercise.place}</p>
      <hr />
      <br />
      <Link
        class="btn btn-primary js-scroll-trigger"
        to={"/getLocation/" + props.exercise._id}
      >
        ShowDetail
      </Link>
    </div>
  </div>
);

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.deleteExercise = this.deleteExercise.bind(this);

    this.state = { exercises: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/exercises/")
      .then((response) => {
        this.setState({ exercises: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteExercise(id) {
    axios.delete("http://localhost:5000/exercises/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      exercises: this.state.exercises.filter((el) => el._id !== id),
    });
  }

  exerciseList() {
    return this.state.exercises.map((currentexercise) => {
      return (
        <Exercise
          exercise={currentexercise}
          deleteExercise={this.deleteExercise}
          key={currentexercise._id}
        />
      );
    });
  }

  render() {
    return (
      <>
        <div class="col-md-12 text-center animate-box">
          <div class="container">{this.exerciseList()}</div>
        </div>

        {/* <section class="projects-section bg-light" id="projects">
          <div class="container">{this.exerciseList()}</div>
        </section> */}
      </>
    );
  }
}
